
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b;
		int result;
		
		a = 100;
		b = 50;
		
		result = a + b; // 100
		System.out.println(a + " + " + b + " = " + result);
		// 100 + 50 =150
		result = a - b; //50
		System.out.println(a + " - " + b + " = " + result);
		// 100 - 50 = 50
		result = a * b; // 5000
		System.out.println(a + " * " + b + " = " + result);
		result = a / b; // 2
		System.out.println(a + " / " + b + " = " + result);
	}

}
