
public class Main_06_02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.printf("byte 형의 크리 ==>%d\t\t", Byte.SIZE);  // size 이런 필드라고 해. 이건 상수이기 때문에 값이 다 나와 있어.
		System.out.printf("short 형의 크리 ==>%d\t\t", Short.SIZE);
		System.out.printf("int 형의 크리 ==>%d\t\t", Integer.SIZE);
		System.out.printf("long 형의 크리 ==>%d\t\t", Long.SIZE);
		System.out.printf("float 형의 크리 ==>%d\t\t", Float.SIZE);
		System.out.printf("double 형의 크리 ==>%d\t\t", Double.SIZE);
		System.out.printf("char 형의 크리 ==>%d\t\t", Character.SIZE);
			

	}

}
